mapper.select:



				and m.menu_name like CONCAT('%',#{menuName},'%')

            and m.menu_status = #{menuStatus}

		order by m.parent_id, m.sort

select distinct m.menu_id, m.parent_id, m.menu_name, m.path, m.component, m.visible, m.menu_status, ifnull(m.perms,'') as perms, m.is_frame, m.menu_type, m.icon, m.sort, m.create_time from sys_menu m left join sys_role_menu rm on m.menu_id = rm.menu_id left join sys_user_role ur on rm.role_id = ur.role_id left join sys_role ro on ur.role_id = ro.role_id
ur.user_id = #{userId}
and m.menu_name like CONCAT('%',#{menu.menuName},'%')
and menu_status = #{menu.menuStatus}
order by m.parent_id, m.sort
